CREATE TABLE dev_area_switchboard_ip(                              	id varchar(32) not null comment '主键',	area_name varchar(100) not null comment '区域名称',	area_describe varchar(500) comment '区域描述',	main_switchboard_ip varchar(20) not null comment '主汇聚交换机ip',	backup_switchboard_ip varchar(20) not null comment '备汇聚交换机ip',	create_time datetime comment '创建时间',	create_user varchar(32) comment '创建人员id',	update_time datetime comment '修改时间',	update_user varchar(32) comment '修改人员id',	state tinyint default 1 comment '状态 0 代表已删除，1代表正常',	primary key (id)) ENGINE=InnoDB DEFAULT CHARSET=UTF8 ROW_FORMAT=DYNAMIC collate=utf8_bin COMMENT '区域汇聚交换机ip对应表';