CREATE TABLE dev_area_switchboard_ip(                              	id varchar(32) not null comment '主键',	area_name varchar(100) not null comment '区域名称',	area_describe varchar(500) comment '区域描述',	main_switchboard_ip varchar(20) not null comment '主汇聚交换机ip',	backup_switchboard_ip varchar(20) not null comment '备汇聚交换机ip',		subnet varchar(100) comment '网段',	dev_name varchar(100) comment '主设备名',	dev_type varchar(100) comment '主设备类型',	dev_factory varchar(100) comment '主设备厂商',	telnet_type varchar(20) comment '主登陆协议',	telnet_user varchar(50) comment '主登陆用户',	telnet_pwd varchar(50)comment '主登陆密码',	telnet_port varchar(50) comment '主端口',	dev_hardware_type varchar(50)comment '主硬件类型',	dev_manager varchar(50)comment '主负责人',		backup_dev_name varchar(100) comment '备设备名',	backup_dev_type varchar(100) comment '备设备类型',	backup_dev_factory varchar(100) comment '备设备厂商',	backup_telnet_type varchar(20) comment '备登陆协议',	backup_telnet_user varchar(50) comment '备登陆用户',	backup_telnet_pwd varchar(50)comment '备登陆密码',	backup_telnet_port varchar(50) comment '备端口',	backup_dev_hardware_type varchar(50)comment '备硬件类型',	backup_dev_manager varchar(50)comment '备负责人',		create_time datetime comment '创建时间',	create_user varchar(32) comment '创建人员id',	update_time datetime comment '修改时间',	update_user varchar(32) comment '修改人员id',	state tinyint default 1 comment '状态 0 代表已删除，1代表正常',	primary key (id)) ENGINE=InnoDB DEFAULT CHARSET=UTF8 ROW_FORMAT=DYNAMIC collate=utf8_bin COMMENT '区域汇聚交换机ip对应表';