CREATE TABLE dev_exclusive_switchboard_info(                            	id varchar(32) not null comment '主键',	exclusive_switchboard_ip varchar(20) comment '带外交机ip（console口ip）',	exclusive_switchboard_port varchar(10) comment '带外交机端口（console口）',	exclusive_switchboard_order smallint comment '带外交机顺序',	exclusive_switchboard_note varchar(1000) comment '带外交机要信息备注',	exclusive_switchboard_type varchar(50) comment '类型',		manager_switchboard_ip varchar(50) comment '管理口ip',	exclusive_switchboard_port_state smallint comment '端口连接状态 0 空闲，1代表实占',		create_time datetime comment '创建时间',	create_user varchar(32) comment '创建人员id',	update_time datetime comment '修改时间',	update_user varchar(32) comment '修改人员id',	state tinyint default 1 comment '状态 0 代表已删除，1代表正常',	primary key (id)) ENGINE=InnoDB DEFAULT CHARSET=UTF8 ROW_FORMAT=DYNAMIC collate=utf8_bin COMMENT '带外交换机信息表';