CREATE TABLE dev_script_config(                              	id varchar(32) not null comment '主键',	object_id varchar(32) not null comment '对像id',	dev_type tinyint not null comment '设备类型：1、接入交换机； 2 、主汇聚交换机； 3 、备汇聚交换机',	script_type tinyint not null comment '脚本类型 1配制文件',	script_order smallint default 1 comment '脚本顺序',	script_info varchar(1000) comment '脚本内容',	create_time datetime comment '创建时间',	create_user varchar(32) comment '创建人员id',	update_time datetime comment '修改时间',	update_user varchar(32) comment '修改人员id',	state tinyint default 1 comment '状态 0 代表已删除，1代表正常',	primary key (id)) ENGINE=InnoDB DEFAULT CHARSET=UTF8 ROW_FORMAT=DYNAMIC collate=utf8_bin COMMENT '设备脚本配制信息';