mapper.insert:
insert into dev_brand_model(
	id,						brand_name,					model_name,				ios_version,		
	ios_version_2,			create_time,				state,					create_user
) values(
	#{model.id},			#{model.brandName},			#{model.modelName},		#{model.iosVersion}, 					
	#{model.iosVersion2},	now(),		 				1,						#{model.create_user}
)

mapper.select:
select 
	brand_name AS brandName,
	model_name AS modelName,
	ios_version AS iosVersion,
	ios_version_2 AS iosVersion2,
	model_describe AS modelDescribe,
	showkvm_describe AS showkvmDescribe
from dev_brand_model
where 1=1

    AND id = #{model.id}


    AND brand_name = #{model.brandName}


    AND model_name = #{model.modelName}


    AND model_describe = #{model.modelDescribe}
