mapper.select:
select id AS id, create_time AS create_time, create_user AS create_user, update_time AS update_time, update_user AS update_user, state AS state, exclusive_switchboard_ip AS exclusiveSwitchboardIp, exclusive_switchboard_port AS exclusiveSwitchboardPort, exclusive_switchboard_order AS exclusiveSwitchboardOrder, exclusive_switchboard_note AS exclusiveSwitchboardNote, exclusive_switchboard_type AS exclusiveSwitchboardType, manager_switchboard_ip AS managerSwitchboardIp, exclusive_switchboard_port_state AS exclusiveSwitchboardPortState, telnet_type AS telnetType, telnet_user AS telnetUser, telnet_pwd AS telnetPwd, telnet_port AS telnetPort
from dev_exclusive_switchboard_info where state=1
AND id = #{info.id}
AND exclusive_switchboard_port_state = #{info.exclusiveSwitchboardPortState}
AND exclusive_switchboard_ip = #{info.exclusiveSwitchboardIp}
AND exclusive_switchboard_port = #{info.exclusiveSwitchboardPort}

mapper.update:
update dev_exclusive_switchboard_info
update_time = now(), exclusive_switchboard_port_state=#{info.exclusiveSwitchboardPortState}, state=#{info.state},
where state=1
AND exclusive_switchboard_ip = #{info.exclusiveSwitchboardIp}
AND exclusive_switchboard_port = #{info.exclusiveSwitchboardPort}
