mapper.insert:
insert into dev_ios_versions(
	id,						brand_name,					model_name,				ios_version,	create_user	
	ios_version_2,			newth_ios_version,			create_time,			state
) values(
	#{version.id},			#{version.brandName},		#{version.modelName},	#{version.iosVersion}, 	#{version.create_user}				
	#{version.iosVersion2},	#{version.newthIosVersion},		now(),		 		1			
)

mapper.delete:
delete from dev_ios_versions
where 1=1

    AND id = #{id}

mapper.select:
select brand_name AS brandName, model_name AS modelName, ios_version AS iosVersion, ios_version_2 AS iosVersion2, newth_ios_version AS newthIosVersion, id, create_time, create_user, update_user, update_time, state from dev_ios_versions where 1=1
AND id = #{version.id}
AND brand_name = #{version.brandName}
AND model_name = #{version.modelName}
