mapper.insert:
insert into dev_ios_versions(
	id,						brand_name,					model_name,				ios_version,	create_user	
	ios_version_2,			newth_ios_version,			create_time,			state,
) values(
	#{version.id},			#{version.brandName},		#{version.modelName},	#{version.iosVersion}, 					
	#{version.iosVersion2},	#{version.newthIosVersion},		now(),		 		1,				#{version.createUser}
)

mapper.delete:
delete from dev_ios_versions
where 1=1

    AND id = #{id}

mapper.select:
select * from dev_ios_versions
where 1=1

    AND id = #{version.id}


    AND brand_name = #{version.brandName}


    AND model_name = #{version.modelName}
