mapper.insert:
insert into dev_online_task_itil(
	id,						itil_number,				dev_online_task_id,		create_time,	state,	create_user
) values(
	#{taskItil.id}, 		#{taskItil.itilNumber},	#{taskItil.devOnlineTaskId},	now(),		 1,		#{taskItil.create_user}
)

mapper.select:
select 
	id AS id,
	itil_number ASitilNumber,
	dev_online_task_id AS devOnlineTaskId

from dev_online_task_itil where 1=1

	and dev_online_task_id = #{taskId}


	and itil_number = #{itilNumber}


	and id = #{id}

mapper.delete:
delete from dev_online_task_itil
where 1=1

	and itil_number = #{itilNumber}


	and dev_online_task_id = #{taskId}
