mapper.insert:
insert into dev_online_task_itil(
	id,						itil_number,				dev_online_task_id,		create_time,	state
) values(
	#{taskItil.id}, 		#{taskItil.itilNumber},	#{taskItil.devOnlineTaskId},	now(),		 1
)

mapper.select:
select * from dev_online_task_itil where 1=1

	and dev_online_task_id = #{taskId}


	and itil_number = #{itilNumber}


	and id = #{id}

mapper.delete:
delete from dev_online_task_itil
where 1=1

	and itil_number = #{itilNumber}


	and dev_online_task_id = #{taskId}
