mapper.select:
SELECT * from( SELECT t.id AS id, t.task_type AS taskType, t.group_key AS groupKey, t.task_describe AS taskDescribe, t.task_state AS taskState, t.brand_name AS brandName, t.model_name AS modelName, t.area_name AS areaName, t.area_describe AS areaDescribe, t.host_name AS hostName, t.manager_ip AS managerIp, t.vlan AS vlan, t.main_switchboard_ip AS mainSwitchboardIp, t.main_switchboard_port AS mainSwitchboardPort, t.backup_switchboard_ip AS backupSwitchboardIp, t.backup_switchboard_port AS backupSwitchboardPort, t.dev_online_rack AS devOnlineRack, t.exclusive_switchboard_ip AS exclusiveSwitchboardIp, t.exclusive_switchboard_port AS exclusiveSwitchboardPort, t.create_time AS createTime, t.update_time AS updateTime, t.username AS createUser, t.manager_ip_write AS managerIpWrite, t.access_config_write AS accessConfigWrite, t.exclusive_switchboard_info AS exclusiveSwitchboardInfo, t.current_ios_version AS currentIosVersion, t.main_switchboard_config_write AS mainSwitchboardConfigWrite, t.model_ios_version AS modelIosVersion, t.access_version_write AS accessVersionWrite, t.switch_state AS switchState, t.backup_switchboard_config_write AS backupSwitchboardConfigWrite, t.access_switchboard_check AS accessSwitchboardCheck
FROM dev_online_task t WHERE t.state=1 ) taskView where 1=1
AND id = #{taskView.id}
AND executeStep = #{taskView.executeStep}
AND switchState = #{taskView.switchState}

select t.id AS id, t.task_type AS taskType, t.group_key AS groupKey, t.task_describe AS taskDescribe, t.task_state AS taskState, t.brand_name AS brandName, t.model_name AS modelName, t.area_name AS areaName, t.area_describe AS areaDescribe, t.host_name AS hostName, t.manager_ip AS managerIp, t.vlan AS vlan, t.main_switchboard_ip AS mainSwitchboardIp, t.main_switchboard_port AS mainSwitchboardPort, t.backup_switchboard_ip AS backupSwitchboardIp, t.backup_switchboard_port AS backupSwitchboardPort, t.dev_online_rack AS devOnlineRack, t.exclusive_switchboard_ip AS exclusiveSwitchboardIp, t.exclusive_switchboard_port AS exclusiveSwitchboardPort, t.manager_ip_write AS managerIpWrite, t.access_config_write AS accessConfigWrite, t.exclusive_switchboard_info AS exclusiveSwitchboardInfo, t.current_ios_version AS currentIosVersion, t.main_switchboard_config_write AS mainSwitchboardConfigWrite, t.model_ios_version AS modelIosVersion, t.access_version_write AS accessVersionWrite, t.switch_state AS switchState, t.backup_switchboard_config_write AS backupSwitchboardConfigWrite, t.access_switchboard_check AS accessSwitchboardCheck, t.update_user AS update_user, t.usercode AS usercode, t.username AS userName
from dev_online_task t where state=1
AND t.id = #{id}

select t.id AS id, t.task_type AS taskType, t.group_key AS groupKey, t.task_describe AS taskDescribe, t.task_state AS taskState, t.brand_name AS brandName, t.model_name AS modelName, t.area_name AS areaName, t.area_describe AS areaDescribe, t.host_name AS hostName, t.manager_ip AS managerIp, t.vlan AS vlan, t.main_switchboard_ip AS mainSwitchboardIp, t.main_switchboard_port AS mainSwitchboardPort, t.backup_switchboard_ip AS backupSwitchboardIp, t.backup_switchboard_port AS backupSwitchboardPort, t.dev_online_rack AS devOnlineRack, t.exclusive_switchboard_ip AS exclusiveSwitchboardIp, t.exclusive_switchboard_port AS exclusiveSwitchboardPort, t.manager_ip_write AS managerIpWrite, t.access_config_write AS accessConfigWrite, t.exclusive_switchboard_info AS exclusiveSwitchboardInfo, t.current_ios_version AS currentIosVersion, t.main_switchboard_config_write AS mainSwitchboardConfigWrite, t.model_ios_version AS modelIosVersion, t.access_version_write AS accessVersionWrite, t.switch_state AS switchState, t.backup_switchboard_config_write AS backupSwitchboardConfigWrite, t.access_switchboard_check AS accessSwitchboardCheck, t.update_user AS update_user, t.usercode AS usercode
from dev_online_task t where state=1
AND t.id = #{task.id}
AND t.manager_ip = #{task.managerIp}
