mapper.select:
select id AS id, handler_name AS handlerName, switchboard_ip AS switchboardIp, port_mode_vlan AS portModeVlan, command, method, execute_state AS executeState, execute_info AS executeInfo, create_time AS createTime, state from dev_port_command_info where state=1
AND switchboard_ip = #{port.switchboardIp}
AND port_mode_vlan = #{port.portModeVlan}
AND task_id = #{port.taskId}
order by create_time asc

mapper.insert:
insert into dev_port_command_info(
	id,			handler_name,		switchboard_ip,			port_mode_vlan,
	execute_state,						state,				create_time,
	command,	 method, 			execute_info ,			task_id
) values (
	#{port.id},	#{port.handlerName}, #{port.switchboardIp}, #{port.portModeVlan},
	#{port.executeState},				1,					now(),
	#{port.command},#{port.method},#{port.executeInfo},		#{port.taskId}
)

mapper.update:
update dev_port_command_info 

	update_time = now(),
	execute_state=#{port.executeState},
	execute_info=#{port.executeInfo},

where state=1

    AND switchboard_ip = #{port.switchboardIp}


    AND port_mode_vlan = #{port.portModeVlan}


    AND task_id = #{port.taskId}


    AND id = #{port.id}

mapper.select:
select id AS id, handler_name AS handlerName, switchboard_ip AS switchboardIp, port_mode_vlan AS portModeVlan, command, method, execute_state AS executeState, execute_info AS executeInfo, create_time AS createTime, state from dev_port_command_info where state=1
AND switchboard_ip = #{port.switchboardIp}
AND port_mode_vlan = #{port.portModeVlan}
AND task_id = #{port.taskId}
order by create_time
