mapper.insert:
insert into dev_script_config(
	id,						object_id,					dev_type,				script_type,		
	script_order,			script_info,				create_time,			state,			create_user
) values(
	#{config.id},			#{config.objectId},			#{config.devType},		#{config.scriptType}, 					
	#{config.scriptOrder},	#{config.scriptInfo},		now(),		 			1,				#{config.create_user}
)

mapper.select:
select object_id AS objectId, dev_type AS devType, script_type AS scriptType, script_order AS scriptOrder, script_info AS scriptInfo
from dev_script_config where 1=1
object_id = #{config.objectId}
dev_type = #{config.devType}
order by script_order asc
