mapper.select:
SELECT id, concat(ip_segment,'/',subnet_mask) AS ipSegment, ip_segment as ip, subnet_mask AS subnetMask, address, network_type AS networkType, use_type AS useType, dev_type AS devType, used_ip AS usedIp, leisure_ip AS leisureIp, remark, responsible, update_user_info AS updateUserInfo, create_time AS createTime, create_user AS createUser, update_time AS updateTime, update_user AS updateUser, state FROM dev_ip_segment_distribution WHERE state = 1
and id = #{para.id}
and network_type = #{para.networkType}
and use_type = #{para.useType}
and ip_segment = #{para.ipSegment}
and subnet_mask = #{para.subnetMask}
and dev_type = #{para.devType}
order by ${sortBy}
ORDER BY update_time desc,inet_aton(ip_segment)
${order}
asc
limit ${limit}

select count(*) from dev_ip_segment_distribution where state=1


    and id = #{para.id}


    and ip_segment = #{para.ipSegment}


    and subnet_mask = #{para.subnetMask}


    and network_type = #{para.networkType}


    and use_type = #{para.useType}


    and dev_type = #{para.devType}

SELECT network_type as networkType, use_type as useType, dev_type as devType, ip_address_type as ipAddressType, user_plan as userPlan, category
FROM dev_ip_segment_config WHERE state = 1
and id = #{para.id}
and network_type = #{para.networkType}
and use_type = #{para.useType}
and dev_type = #{para.devType}

SELECT id, address AS address, ip AS ip, ip_segment AS ipSegment, network_type AS networkType, use_type AS useType, dev_type AS devType, category AS category, ip_address_type AS ipAddressType, dev_name AS devName, dev_model AS devModel, user_plan AS userPlan, vlan AS vlan, subnet_mask AS subnetMask, gateway AS gateway, area AS area, start_node AS startNode, end_node AS endNode, start_port AS startPort, end_port AS endPort, remark AS remark, responsible AS responsible, allocation_user AS allocationUser, allocation_time AS allocationTime, update_user_info AS updateUserInfo, CASE is_Use WHEN 1 THEN '使用' WHEN 0 THEN '未使用' END AS isUse, create_time AS createTime, create_user AS createUser, update_time AS updateTime, update_user AS updateUser FROM dev_ip_distribution WHERE state = 1
and id = #{para.id}
and ip = #{para.ip}
and is_use = ${para.isUseInteger}
and is_use = ${para.isUse}
and ip_segment = #{para.ipSegment}
and network_type = #{para.networkType}
and use_type = #{para.useType}
and dev_type = #{para.devType}
order by ${sortBy}
ORDER BY update_time desc, inet_aton(ip_segment) asc, is_Use desc , inet_aton(ip )
${order}
asc
limit ${limit}

select count(*) from dev_ip_distribution where state=1


    and id = #{para.id}


    and ip = #{para.ip}


    and is_use = ${para.isUseInteger}


    and is_use = ${para.isUse}


    and ip_segment = #{para.ipSegment}


    and network_type = #{para.networkType}



    and use_type = #{para.useType}


    and dev_type = #{para.devType}

mapper.insert:
UPDATE dev_ip_segment_distribution set state=0 where ip_segment=#{para.ipSegment};
insert into dev_ip_segment_distribution( id , ip_segment , subnet_mask , address , network_type , use_type , dev_type , used_ip , leisure_ip , remark , responsible , update_user_info , create_time , create_user , update_time , update_user , state ) values
( #{para.id}, #{para.ipSegment}, #{para.subnetMask}, #{para.address}, #{para.networkType}, #{para.useType}, #{para.devType}, #{para.usedIp}, #{para.leisureIp}, #{para.remark}, #{para.responsible}, #{para.updateUserInfo}, now(), #{para.create_user}, now(), #{para.update_user}, 1 )

