mapper.select:
select id AS id, usercode AS userCode, username AS userName, password AS passWord, email AS email, mobile_phone AS mobilePhone, locked AS locked, department AS department, send_email AS sendEmail
from sys_user where 1=1
AND id = #{user.id}
AND usercode = #{user.userCode}
AND department = #{user.department}
AND send_email = #{user.sendEmail}
